import React, {useState} from "react";
import {getProduct} from "../../api/listaProductos";

function Search(props){
    const {childClicked}=props;

    async function searchProduct(){
        let query= document.getElementById("filterProduct").value;
        const response = await getProduct(query);
        // childClicked(response);
        console.log(response);
    }
    return(
        <div className="content-wrapper">
            <div className="pt-3">
                <div className="container-fluid">
                    <div className="row" >
                        <div className="col-md-12 d-flex">
                            <input className="form-control me-2" id="filterProduct" type="search" placeholder="Search Product" aria-label="Search"></input>
                            <button className="btn btn-primary" onClick={()=>searchProduct()} >Search</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
export default Search;
