import React,{useState} from 'react'

export default function Product(props) {
    const {id_prod, nombre, cod, stock, price, enabled, image} = props;
    // console.log(storeId, accessToken);

    // se usa para mostrar el icono y titulo de las opciones de cada producto
    const [opciones,setopciones]=useState(false);
    // ======================================================

    // almacena el estado del chek para mostrar u ocultar los componentes de editar precio
    const [estadoCheck, setCheck]=useState(enabled);
    
    function changeCheck(){
        // var estadoI= document.getElementById("chk_state");
        // if(!estadoCheck == true){
        //     console.log(`ID: ${id_prod}`, `Estado: ${estadoCheck}`);
        // }
        statusCheck = (!estadoCheck === true) ? true : false;
        console.log(statusCheck);
        // changeStatusProduct();
        setCheck(!estadoCheck);
    }
    function changeStatusProduct(){
        // const storeData = JSON.parse(localStorage.getItem("storeData"));
        // const storeId = storeData.store_id;
        // const accessToken = storeData.access_token;

        // const options = {
        //     method: 'PUT',
        //     headers: {
        //         Accept: 'application/json',
        //         Authorization: `Bearer ${accessToken}`,
        //         'Content-Type': 'application/json'
        //     },
        //     payloads: {
        //         id: id_prod,
        //         price: price
        //     }
        // };
          
        // fetch('https://app.ecwid.com/api/v3/storeId/products/productId', options)
        // .then(response => response.json())
        // .then(response => console.log(response))
        // .catch(err => console.error(err));
    }
    // =============================================================

    // Muestra Precios
    const [pPrice, setPPrice] = useState(true);
    const [txtPrice, settxtPrice] = useState(false);

    // Muestra los botones de accion
    const [buttonE, setButtonE] = useState(true);
    const [buttonA, setButtonA] = useState(false);

    function actions(action){
        if(action === "edit"){
            var sPrice = document.getElementById('sPrice').textContent;
            document.getElementById('txtPrice').value = sPrice;
            setButtonE(false);
            setButtonA(true);
            setPPrice(false);
            settxtPrice(true);
        }else{
            if(action === "save"){
                console.log("guarda");
                var txtPrice = document.getElementById('txtPrice').value;
                document.getElementById('sPrice').textContent = txtPrice;
            }
            setButtonE(true);
            setButtonA(false);
            setPPrice(true);
            settxtPrice(false);
        }


        // var save = document.getElementById("btnSave");
        // var cancel = document.getElementById("btnCancel");
    }
    
    return (
        <div className="content-wrapper">
            <div className="pt-3">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="card" >
                                <div className="row g-0" style={{paddingTop:10,paddingBottom:10}}>
                                    <div className='col-md-1' style={{alignSelf:"center", display:"flex", justifyContent:"center", alignItems:"center"}} >
                                        <div className="form-check " >
                                            <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault"></input>
                                        </div>
                                    </div>
                                    <div className="col-md-1" style={{color:'GrayText'}}>
                                        <img src={image===null || image===undefined || image==="" ? ("http://bppl.kkp.go.id/uploads/publikasi/karya_tulis_ilmiah/default.jpg"):(image)  } className="img-fluid rounded-start" alt="Product Image" width={"100%"}></img>
                                    </div>
                                    <div className="col-md-10">
                                        <div className="card-body" >
                                            <div className='row'>
                                                <div className='col-md-6'>
                                                    <h5 className="card-title">{nombre} <small className="text-muted">{cod}</small></h5>
                                                    <div className='row'>
                                                        <div className='col-md-8'>
                                                            <div className='input-group'style={{justifyContent:'space-evenly'}}>
                                                                <div className="form-check form-switch">
                                                                    <input className="form-check-input" onClick={()=>changeCheck()} type="checkbox" id={id_prod} checked={estadoCheck}></input>
                                                                    <label className="form-check-label" htmlFor={id_prod}>{
                                                                        estadoCheck?"Activado":"Desactivado"
                                                                    }</label>
                                                                </div>
                                                                <h6 style={{color:(stock > 0) ? "black" : "red"}}>{(stock > 0) ? `Available: ${stock}` : "Unavailable"}</h6>
                                                            </div>  
                                                        </div>
                                                    </div>
                                                    {
                                                        opciones?(<div className='row'>
                                                            <div className='col-md-12'>
                                                                <a href='google.com'><i className="far fa-clone"></i> <label>Opciones</label></a>
                                                            </div>
                                                        </div>
                                                        ):(null)
                                                    }
                                                </div>
                                                <div className='col-md-3 text-center'>
                                                    <h3 hidden={pPrice?(false):(true)}>$<small id="sPrice">{price}</small></h3>
                                                    <input className="form-control" id="txtPrice" placeholder='New Price' hidden={txtPrice?(false):(true)}></input>
                                                </div>
                                                <div className='col-md-3 text-center'>
                                                    <div className="btn-group" role="group" aria-label="Basic example">
                                                        <button type="button" className="btn btn-primary" onClick={()=>actions('edit')} hidden={buttonE?(false):(true)}>Edit Product</button>
                                                        <button type="button" className="btn btn-success" onClick={()=>actions('save')} hidden={buttonA?(false):(true)}>Save Changes</button>
                                                        <button type="button" className="btn btn-danger" onClick={()=>actions('cancel')} hidden={buttonA?(false):(true)}>Cancel</button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );

    
}
