import React, { useEffect, useState } from "react";
// import "./App.css";
import Search from "./components/search/Search";
import Shipping from "./components/Shipping/Shipping";
import Product from "./components/product_detail/Product";
function App() {
  // Get the store ID and access token
  useEffect(() => {
    const script = document.createElement("script");

    script.src =
      "https://djqizrxa6f10j.cloudfront.net/ecwid-sdk/js/1.2.9/ecwid-app.js";

    script.async = false;
    document.body.appendChild(script);

    EcwidApp.init({
      app_id: "testapp2", // your client_id
      autoloadedflag: true,
      autoheight: true,
    });

    // Get the store ID and access token
    var storeData = EcwidApp.getPayload();
    var storeId = storeData.store_id;
    var accessToken = storeData.access_token;
    var language = storeData.lang;

    console.log('Token: ', accessToken);

    if (storeData.public_token !== undefined) {
      var publicToken = storeData.public_token;
    }

    if (storeData.app_state !== undefined) {
      var appState = storeData.app_state;
    }

    return () => {
      // clean up the script when the component in unmounted
      document.body.removeChild(script);
    };
  }, []);
  return (
    <div className="App">
      <Search/>
      <Shipping/>
      <Product/>
    </div>
  );
}

export default App;
