import React, { useEffect, useState } from "react";
// import "./App.css";
import Search from "./components/search/Search";
import Shipping from "./components/Shipping/Shipping";
import Product from "./components/product_detail/Product";
function App() {

  // Get the store ID and access token
  useEffect(() => {
    const script = document.createElement("script");
    const storeData = localStorage.getItem('storeData');
    var storeId = storeData.store_id;
    var accessToken = storeData.access_token;

    script.src =
      "https://djqizrxa6f10j.cloudfront.net/ecwid-sdk/js/1.2.9/ecwid-app.js";

    script.async = false;
    document.body.appendChild(script);



    return () => {
      // clean up the script when the component in unmounted
      document.body.removeChild(script);
    };
  }, []);
  return (
    <div className="App">
      <Search/>
      <Shipping/>
      <Product storeId={storeId} accessToken={accessToken} nombre="Porta retrato" cod="RC005" stock={10} price={15.26} />
    </div>
  );
}

export default App;
